import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import SceneContext from '../component/SceneContext.js'
import React,{useContext} from 'react';


function MyVerticallyCenteredModal(props) {
  const {scene,setScene} = useContext(SceneContext)
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Заявка № 219266
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h4>Покупка 100 USDT</h4>
        <p>
          <ul>
            <li>Оплата на Тиньков 63204 руб</li>
            <li>Ваш адрес зачисления BEP20 0xa6f79B60359f141d...</li>
            <li>Ваш Telegram для связи @DimaRamp</li>
          </ul>
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-dark" onClick={()=>{props.onHide();setScene(0)}}>Отмена</Button>

          <Modal2 />

      </Modal.Footer>
    </Modal>
  );
}

function App() {
  const [modalShow, setModalShow] = React.useState(false);

  return (
    <>
      <Button variant="outline-dark" size="lg" onClick={() => setModalShow(true)} style={{width: '330px'}}>
        Проверить заявку
      </Button>

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </>
  );
}

export default App;
