    // }



    function handleChange(event){
      //console.log(event.target.value)
      // setFlagCalc(0)
      setValueAsset(event.target.value)
      setFlagCalc('asset')

    }

    return (
      <Box
        component="form"
        sx={{
          '& .MuiTextField-root': { m: 1, width: '330px' },
