import React,{useState,useEffect,useMemo} from 'react';
import Container from 'react-bootstrap/Container';

import Scene0 from './scene/scene0.js'
import Scene1 from './scene/scene1.js'
import Scene2 from './scene/scene2.js'
import Scene3 from './scene/scene3.js'

import SceneContext from './context/SceneContext.js'
import TypeContext from './context/TypeContext.js'
import AssetContext from './context/AssetContext.js'
import TypePayContext from './context/TypePayContext.js'
import AssetLimitContext from './context/AssetLimitContext.js'
import PriceBuyContext from './context/PriceBuyContext.js'
import PriceSellContext from './context/PriceSellContext.js'

import ValueAssetContext from './context/ValueAssetContext.js'
import ValueFiatContext from './context/ValueFiatContext.js'

//DataContext
import DataContext from './context/GlobalContext.js'


import axios from "axios";

function App() {
  console.log('Render APP')
  const [scene,setScene] = useState(0)
  const [typeRequest,setTypeRequest] = useState()

  const [asset,setAsset] = useState('')

  const [typePay,setTypePay] = useState('')


  const [price,setPrice] = useState('')


  const [valueAsset,setValueAsset] = useState(0)
  const [valueFiat,setValueFiat] = useState(0)


  const [assetLimit,setAssetLimit] = useState(['Перезагрузи'])
  const [typePayList,setTypePayList] = useState(['Перезагрузи'])

  //install dataContext

  const GlobalContext={
    "scene":{scene,setScene},
    "typeRequest":{typeRequest,setTypeRequest},
  }

  //instal qurey
  useMemo(async ()=>{
    console.log('useMemo install qurey')

    let {data} = await axios.get('http://cherdak.fun:3001/api/price/full')

    GlobalContext.price.setPrice(data)


    let {data: res2} = await axios.get('http://cherdak.fun:3001/api/asset')
    GlobalContext.assetLimit.setAssetLimit(res2)

    let {data: res3} = await axios.get('http://cherdak.fun:3001/api/paytypes')
    GlobalContext.typePayList.setTypePayList(res3)


    async function getPrice(){
      let {data} = await axios.get('http://cherdak.fun:3001/api/price/full')
      //console.log(data)
      //sessionStorage.setItem('price',JSON.stringify(data))
      GlobalContext.price.setPrice(data)
    }

    setInterval(getPrice,3000)
  },[])





  function getScene(){
    console.log('getScene')
      switch(GlobalContext.scene.scene){
        case 0:
          return <Scene0/>
        case 1:
          return  <Scene1/> 
        case 2:
          return <Scene2/> 
        case 3:
          return <Scene3/> 
      }

  }

  console.log(GlobalContext)
  return (

    <SceneContext.Provider value={{scene,setScene}}>
    <TypeContext.Provider value={{type: typeRequest,setType: setTypeRequest}}>
    <AssetContext.Provider value={{asset,setAsset}}>
    <TypePayContext.Provider value={{typePay,setTypePay}}>
    <AssetLimitContext.Provider value={{assetLimit,setAssetLimit}}>


    <ValueAssetContext.Provider value={{valueAsset,setValueAsset}}>
    <ValueFiatContext.Provider value={{valueFiat,setValueFiat}}>



    <DataContext.Provider value={GlobalContext}>


      <Container style={{height: "100vh"}}>
        {getScene()}
      </Container>
    </DataContext.Provider>



    </ValueFiatContext.Provider>
    </ValueAssetContext.Provider>

    </AssetLimitContext.Provider>
    </TypePayContext.Provider>
    </AssetContext.Provider>
    </TypeContext.Provider>
    </SceneContext.Provider>
  );
}

export default App;
