import React,{useState,useEffect} from 'react';

import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import AssetContext from '../../context/AssetContext.js'

import TypeContext from '../../context/TypeContext.js'
import AssetLimitContext from '../../context/AssetLimitContext.js'
import axios from "axios";


export default  function SelectAsset() {
  // const [asset, setAsset] = React.useState('');
  const {asset,setAsset} = React.useContext(AssetContext)

  const {type,setType} = React.useContext(TypeContext)

  const {assetLimit,setAssetLimit} = React.useContext(AssetLimitContext)



  const handleChange = (event) => {
    setAsset(event.target.value);
  };

  return (
    <div>
      <FormControl sx={{ m: 1, width: '330px'}}>
        <InputLabel id="demo-simple-select-autowidth-label"
        sx={{
          color: "black",

        }}>{type=='Buy'?"Получаете":"Отдаёте"}</InputLabel>
        <Select
          labelId="demo-simple-select-autowidth-label"
          id="demo-simple-select-autowidth"
          value={asset}
          onChange={handleChange}
          autoWidth
          label={type=='Buy'?"Получаете":"Отдаёте"}
          sx={{

          }}
        >
          {Object.keys(assetLimit).map((item)=>{
            return <MenuItem key={Math.random()} value={item}>{item}</MenuItem>
          })}


        </Select>
      </FormControl>
    </div>
  );
}
