import React,{useContext,useState,useEffect} from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

import DataContext from '../../context/DataContext.js'

export default function ImputFiat() {
  let dataContext = useContext(DataContext) 

  const {scene,setScene} = dataContext.scene
  const {asset,setAsset} = dataContext.asset
  const {typePay,setTypePay} = dataContext.typePay
  const {assetLimit,setAssetLimit} = dataContext.assetLimit
  const {price,setPrice} = dataContext.price

  const {valueAsset,setValueAsset} = dataContext.valueAsset
  const {valueFiat,setValueFiat} = dataContext.valueFiat

  const {typeRequest,setTypeRequest} = dataContext.typeRequest

  function handleChange(event){
    //console.log(event.target.value)
    setValueFiat(event.target.value)
    setValueAsset(+event.target.value/price[typeRequest.toLowerCase()][asset.toLowerCase()])

  }
  return (
    <Box
      component="form"
      sx={{
        '& .MuiTextField-root': { m: 1, width: '330px' },
      }}
      noValidate
      autoComplete="off"
    >
      <div>
      <TextField 
        label={typePay} 
        value={valueFiat} 
        onChange={handleChange}
        />
      </div>
    </Box>
  );
}
