import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Container from 'react-bootstrap/Container';

import NavBar from '../component/NavBar.js'
import SelectAsset from '../component/sc1/SelectAsset.js'
import SelectTypePay from '../component/sc1/SelectTypePay.js'
import InputAsser from '../component/sc1/InputAsser.js'
import InputFiat from '../component/sc1/InputFiat.js'

import Button from 'react-bootstrap/Button';
import SceneContext from '../context/SceneContext.js'
import TypeContext from '../context/TypeContext.js'
import AssetContext from '../context/AssetContext.js'
import TypePayContext from '../context/TypePayContext.js'

import DataContext from '../context/DataContext.js'

import React,{useContext,useState,useEffect} from 'react';

export default function Scene1(){

  let dataContext = useContext(DataContext) 

  const {scene,setScene} = dataContext.scene
  const {asset,setAsset} = dataContext.asset
  const {typePay,setTypePay} = dataContext.typePay

  const {price,setPrice} = dataContext.price

  const {valueAsset,setValueAsset} = dataContext.valueAsset
  const {valueFiat,setValueFiat} = dataContext.valueFiat
  const {typeRequest,setTypeRequest} = dataContext.typeRequest

    const [flagInput,setFlagInput] = useState(false)
    useEffect(()=>{
      if(asset && typePay){

        setFlagInput(true)

      }else{
        setFlagInput(false)
      }
    },[asset,typePay])

    const [flagCalc,setFlagCalc] = useState(-1)

    useEffect(()=>{
      if(flagCalc==1){

      }
      if(flagCalc==0){
        console.log('adsssssssssssssss')

        setInterval(()=>{
          setValueFiat(valueAsset*price[typeRequest.toLowerCase()][asset.toLowerCase()] )
        },3000)

        //   +event.target.value*price[typeRequest.toLowerCase()][asset.toLowerCase()]
        //   )
      }

      return ()=>{

      }
    },[flagCalc])
    return(
      <>
        <Container style={{height: '20%'}}>

            <Row style={{paddingTop:"5vh"}}>
              <div className="d-flex justify-content-center">
                <h1><b onClick={()=>{
                  setScene(0);
                  setAsset('');
                  setTypePay('');
                  setTypePay();
                  }}>Cherdak.fun</b></h1>
              </div>
            </Row>
          <NavBar />  
        </Container>
        <Container style={{height: '60%'}} className="d-flex align-items-center">
          <Container>

            <Row>
              <Col>
                  <div className="d-flex justify-content-center">
                    {
                      <SelectAsset/>
                    }
                  </div>
              </Col>
            </Row>
            <Row>
              <Col>
              <div className="d-flex justify-content-center">
                {
                  flagInput
                    ?<InputAsser setFlagCalc={setFlagCalc}/>              
                    :null
                }
                </div>
              </Col>
            </Row>
            <Row>
              <Col>
                <div className="d-flex justify-content-center">
                    {
                      <SelectTypePay/>
                    }
                </div>
              </Col>
            </Row>
            <Row>
              <Col>
              <div className="d-flex justify-content-center">
                { 
                  flagInput
                    ?<InputFiat/>              
                    :null

                }
              </div>
              </Col>
            </Row>
            <Row>

              <Col>
              <div style={{display:flagInput?"block":'none'}}>
                <div className="d-flex justify-content-center">
                  <Button variant="outline-dark" size="lg" onClick={()=>{setScene(2)}}style={{width:'100vh', maxWidth: 330}}>Далее</Button>
                </div>
              </div>
              </Col>
            </Row>
          </Container>
        </Container>
      </>
    )
}
