import React,{useContext,useState,useEffect} from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

import AssetContext from '../../context/AssetContext.js'
import AssetLimitContext from '../../context/AssetLimitContext.js'

import PriceBuyContext from '../../context/PriceBuyContext.js'
import PriceSellContext from '../../context/PriceSellContext.js'

import ValueAssetContext from '../../context/ValueAssetContext.js'
import ValueFiatContext from '../../context/ValueFiatContext.js'

import TypeContext from '../../context/TypeContext.js'

export default function ImputAsset() {

    //const {asset,setAsset} = useContext(AssetContext)
    const {asset,setAsset} = useContext(AssetContext)


    const {assetLimit,setAssetLimit} = useContext(AssetLimitContext)

    const {priceBuy,setPriceBuy} = useContext(PriceBuyContext)
    const {priceSell,setPriceSell} = useContext(PriceSellContext)

    const {valueAsset,setValueAsset} = useContext(ValueAssetContext)
    const {valueFiat,setValueFiat} = useContext(ValueFiatContext)

    const {type,setType} = useContext(TypeContext)

    let limit=''
    let defaultValue='321'
    if(asset!=''){
      limit = ` min:${assetLimit[asset].min} max:${assetLimit[asset].max}`
      defaultValue=assetLimit[asset].min;

      // if(type=='Buy'){
      //   setValueAsset(priceBuy[asset.toLowerCase()])
      // }else{
      //   setValueAsset(priceBuy[asset.toLowerCase()])
      // }
    }



    function handleChange(event){
      console.log(event.target.value)
      setValueAsset(event.target.value)

      let valFiat=0
      if(type=='Buy'){
        setValueFiat(+event.target.value*priceBuy[asset.toLowerCase()])
      }else{
        setValueFiat(+event.target.value*priceSell[asset.toLowerCase()])
      }

      //setValueFiat(valFiat)
    }

    return (
      <Box
        component="form"
        sx={{
          '& .MuiTextField-root': { m: 1, width: '330px' },
        }}
        noValidate
        autoComplete="off"
      >

        <TextField

          label={asset+limit}

          value={valueAsset}
          onChange={handleChange}
        />

      </Box>
    );
  }
